UPDATE MENU_ITEM
SET COD_PAI =
(SELECT M.COD_MENU_ITEM
 FROM MENU_ITEM M
   LEFT JOIN MENU_ITEM P ON P.COD_MENU_ITEM = M.COD_PAI
 WHERE M.DSC_MENU_ITEM = 'Endereço'
       AND P.DSC_MENU_ITEM = 'Cadastros')
WHERE COD_MENU_ITEM =
      (SELECT M.COD_MENU_ITEM
       FROM MENU_ITEM M
         LEFT JOIN MENU_ITEM P ON P.COD_MENU_ITEM = M.COD_PAI
       WHERE M.DSC_MENU_ITEM = 'Imprimir Etiquetas'
             AND P.DSC_MENU_ITEM = 'Produto');






