INSERT INTO PARAMETRO (COD_PARAMETRO, COD_CONTEXTO_PARAMETRO, DSC_PARAMETRO, DSC_TITULO_PARAMETRO, IND_PARAMETRO_SISTEMA, COD_TIPO_ATRIBUTO, DSC_VALOR_PARAMETRO)
SELECT SQ_PARAMETRO_01.NEXTVAL as COD_PARAMETRO, (SELECT COD_CONTEXTO_PARAMETRO FROM CONTEXTO_PARAMETRO WHERE DSC_CONTEXTO_PARAMETRO = 'EXPEDICAO') as COD_CONTEXTO_PARAMETRO, 'REFRESH_INDEX_EXPEDICAO' as DSC_PARAMETRO, 'Refresh Grid Expedição Automaticamente' as DSC_TITULO_PARAMETRO, 'N' as IND_PARAMETRO_SISTEMA, 'A' as COD_TIPO_ATRIBUTO, 'N' as DSC_VALOR_PARAMETRO FROM DUAL
INNER JOIN (SELECT COUNT(*) EXISTE FROM PARAMETRO WHERE DSC_PARAMETRO = 'REFRESH_INDEX_EXPEDICAO') P ON P.EXISTE = '0';