--NOVAS QUERIES PARA INTEGRAÇÃO DE PEDIDOS
DELETE FROM ACAO_INTEGRACAO_ANDAMENTO WHERE COD_ACAO_INTEGRACAO = 3;
DELETE FROM ACAO_INTEGRACAO_FILTRO WHERE COD_ACAO_INTEGRACAO = 3;
