<?php
namespace Wms\Domain\Entity\Deposito;

use Doctrine\ORM\EntityRepository,
    Wms\Domain\Entity\Recebimento as RecebimentoEntity;

/**
 * Recebimento
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RecebimentoRepository extends EntityRepository
{   
    public function save(RecebimentoEntity $recebimentoEntity, array $values)
    {
	extract($values['identificacao']);
	$em = $this->getEntityManager();
	
	$recebimentoEntity->setDeposito($id);
	$recebimentoEntity->setDescricao($dataInicial);
	$recebimentoEntity->setDeposito($DataFinal);
	$recebimentoEntity->setDescricao($status);
	$em->persist($recebimentoEntity);
    }

    public function remove($id)
    {
	$em = $this->getEntityManager();
	$proxy = $em->getReference('wms:Recebimento', $id);
	$em->remove($proxy);
    }
}
